function palindrome(str) {
  // Good luck!

  let fccRegex = /[\W_]/g;

  let someStr = str.toLowerCase().replace(fccRegex, "");



  let reversed = someStr.split('').reverse().join('');
  if(reversed === someStr){
    return true;
    } else{
      return false
      }
}



