function convertToRoman(num) {

 //Creates objects with all the equivalent Roman Numbers.
 var romanNumbers = {  
    M: 1000,
    CM: 900,
    D: 500,  	 
    CD: 400,	
    C: 100, 
    XC: 90,
    L: 50, 
    XL: 40, 
    X: 10, 
    IX: 9,
    V: 5,  	
    IV: 4,
    I: 1 			
 }

 // Empty string to store info.
 var romanString = "";

 // Loops to every letter(l) in romanNumbers.
 for(var l in romanNumbers){

    /* It checks from, top to bottom, if var l matches a number and returns the correct roman letter(l)*/
    while(num >= romanNumbers[l]){
      romanString += l;
      num -= romanNumbers[l]; 
    }
 }

return romanString;

}

